import React from 'react'
import '../styles/bootstrap.min.css'
import '../styles/main.css'
import axios from 'axios'
import {BeritaTerkini} from './BeritaTerkini'

// News API
const apiKey = '9975f97755874c9fb9b501d61a9cff2a'
const baseUrl = 'https://newsapi.org/v2/'
const urlHeadline = baseUrl + "top-headlines?country=id&apiKey=" + apiKey

export class News extends React.Component{
    state = {
        judulBeritaTerkini: [],
        isLoading: true 
    }

    componentDidMount = () => {
        const self = this;
        axios
            .get(urlHeadline)
            .then(function(response){
                self.setState({judulBeritaTerkini: response.data.articles, isLoading: false})
            })
            .catch(function(response){
                self.setState({isLoading: false})
            })
    }

    render(){
        return (
            <BeritaTerkini />
        )
    }
}
