import React from 'react'
import '../styles/bootstrap.min.css'
import '../styles/main.css'
import reactLogo from '../img/logo.svg'
import {SearchBar} from '../components/SearchBar'
import {BrowserRouter, Route, Link} from 'react-router-dom'

export class Header extends React.Component {
    // Define state
    state = {
        keyWord: ''
    }

    // Handle Change
    handleChange = event => {
        console.warn('check handleChange', event)
        this.setState({keyWord: event.target.value})
        console.warn('check keyWord', this.state.keyWord)
    }

    // Searching function
    searchingByKeyword = async keyWord => {
        console.warn("Checking keyWord", keyWord)
        const self = this;
        if(keyWord.length > 2){
            await axios
                .get(baseUrl + "everything?q=" + keyWord + "&apiKey=" + apiKey)
                .then(function(response){
                    self.setState({beritaUtama: response.data.articles, isLoading: false})
                })
                .catch(function(response){
                    self.setState({isLoading: false})
                })
        }
    }

    render(){
        return (
            <header>
                <div className='container-fluid'>
                    <div className='row'>
                        <div className='col-2'>
                            <img src={reactLogo} className='react-logo'/>
                            <span>KabarKabar</span>
                        </div>
                        <div className='col-5'>
                            <ul className='list-unstyled topic-menu'>
                                <li><a href=''>Sepakbola</a></li>
                                <li><a href=''>Ekonomi</a></li>
                                <li><a href=''>Politik</a></li>
                                <li><a href=''>Hiburan</a></li>
                                <li>
                                    <form>
                                        <select className='dropdown-in-header'>
                                            <option value='Lainnya'>Lainnya</option>
                                            <option value='Olahraga'>Olahraga</option>
                                        </select>
                                    </form>
                                </li>
                            </ul>
                        </div>
                        <div className='col-3'>
                            <SearchBar />
                        </div>
                        <div className='col-2'>
                            <ul className='list-unstyled register-menu'>
                                <li><a href=''><Link to='/masuk'>Masuk</Link></a></li>
                                <li><a href=''><Link to='/masuk'>Daftar</Link></a></li>
                            </ul>
                        </div>
                    </div>
                </div>
            </header>
        )
    }
}
