import createStore from 'unistore'
import React from 'react'
import {Redirect} from 'react-router-dom'
import {Header} from './components/Header'
import axios from 'axios'

let inisialization = {
    isLogin: false,
    accKey: '',
    name: '',
    email: '',
    category: 'math',
    keyWord: 'math',
    categoryOrSearch: ''
}

export let store = createStore(inisialization)

export let actions = store => (
{
    clickTab: async (state, something, event) => {
        await store.setState({category: something, categoryOrSearch: 'category'})
        console.warn('check state value of category', this.state.category)
    }
},
{
    searchByWord: async (state, something, event) => {
        await store.setState({keyWord: something, categoryOrSearch: 'search'})
        console.warn('check state value of keyword', this.state.keyWord)
    }
},
{
    handleClick: (state, event) => {
        console.warn("check whether handleClick is execute or not")
        store.setState({
            isLogin: false,
            accKey: '',
            name: '',
            email: ''
        })
        alert("Sukses logout")
        return <Redirect to={{ pathname: "/" }} />;
    }
