import React from 'react'
import '../styles/bootstrap.min.css'
import '../styles/main.css'
import searchLogo from '../img/search.png'

export class SearchBar extends React.Component{
    // Define state
    state = {
        beritaUtama: [],
        keyWord: '',
        isLoading: true
    }

    // Handle Change
    handleChange = event => {
        console.warn('check handleChange', event)
        this.setState({keyWord: event.target.value})
        console.warn('check keyWord', this.state.keyWord)
    }

    // Searching function
    searchingByKeyword = async keyWord => {
        console.warn("Checking keyWord", keyWord)
        const self = this;
        if(keyWord.length > 2){
            await axios
                .get(baseUrl + "everything?q=" + keyWord + "&apiKey=" + apiKey)
                .then(function(response){
                    self.setState({beritaUtama: response.data.articles, isLoading: false})
                })
                .catch(function(response){
                    self.setState({isLoading: false})
                })
        }
    }

    render(){
        return (
            <div className="search-bar">
                <form>
                    <input type="text" onChange={this.handleChange} placeholder="Search" value={this.state.keyWord}></input>
                    <button type="submit"><img src={searchLogo} className="search-logo" /></button>
                </form>
            </div>
        )
    }
}
