import createStore from 'unistore'
import React from 'react'
import {Redirect} from 'react-router-dom'
import {Header} from './components/Header'
import axios from 'axios'

let inisialization = {
    isLogin: false,
    accKey: '',
    name: '',
    email: '',
    category: 'math',
    keyWord: 'math',
    categoryOrSearch: '',
    namaPengguna: '',
    kataKunci: ''
}

export let store = createStore(inisialization)

export let actions = store => (
{
    clickTab: async (state, something, event) => {
        await store.setState({category: something, categoryOrSearch: 'category'})
        console.warn('check state value of category', inisialization.category)
    }
},
{
    searchByWord: async (state, something, event) => {
        await store.setState({keyWord: something, categoryOrSearch: 'search'})
        console.warn('check state value of keyword', inisialization.keyWord)
    }
},
{
    handleClick: (state, event) => {
        console.warn("check whether handleClick is execute or not")
        store.setState({
            isLogin: false,
            accKey: '',
            name: '',
            email: ''
        })
        alert("Sukses logout")
        return <Redirect to={{ pathname: "/" }} />;
    }
},
{   
    postLogin: (state, event) => {
        const namaPengguna = inisialization.namaPengguna;
        const kataKunci = inisialization.kataKunci
        const data = {
          username: namaPengguna,
          password: kataKunci
        };

        const self = this;
        axios
          .post("https://react-challenge.free.beeceptor.com/masuk", data)
          .then(async function (response) {
            console.log(response.data);
            if (response.data.hasOwnProperty("accKey")) {
              console.warn('masuk if');
              await store.setState({accKey: response.data.accKey, isLogin: true, name: response.data.name, email: response.data.email});
              console.warn(inisialization.accKey)
            return <Redirect to={{ pathname: "/profile" }} />
            }
          })
          .catch(function (error) {
            console.log(error);
          });
      }
},
{
    changeInput: (state, event) => {
        console.warn("check event target", event.target.value);
        store.setState({ [event.target.name]: event.target.value });
      }
}
)
