import React from 'react'
import '../styles/bootstrap.min.css'
import {Header} from '../components/Header'
import {BeritaTerkini} from '../components/BeritaTerkini'
import {KontenUtama} from '../components/KontenUtama'

export class MainPage extends React.Component {
    // Define state
    state = {
        keyWord: ''
    }

    // Handle Change
    handleChange = event => {
        console.warn('check handleChange', event)
        this.setState({keyWord: event.target.value})
        console.warn('check keyWord', this.state.keyWord)
    }

    // Searching function
    searchingByKeyword = async keyWord => {
        console.warn("Checking keyWord", keyWord)
        const self = this;
        if(keyWord.length > 2){
            await axios
                .get(baseUrl + "everything?q=" + keyWord + "&apiKey=" + apiKey)
                .then(function(response){
                    self.setState({beritaUtama: response.data.articles, isLoading: false})
                })
                .catch(function(response){
                    self.setState({isLoading: false})
                })
        }
    }

    render() {
        return (
            <div>
                <Header />
                <div className='container'>
                    <div className='row'>
                        <div className='col-6'>
                            <div className='container-fluid'>
                                <div className='row'>
                                    <div className='col-2'></div>
                                    <div className='col-9'>
                                        <BeritaTerkini />
                                    </div>
                                    <div className='col-1'></div>
                                </div>
                            </div>
                        </div>
                        <div className='col-6'>
                            <div className='container-fluid'>
                                <div className='row'>
                                    <div className='col-10'>
                                        <KontenUtama />
                                    </div>
                                    <div className='col-2'></div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
