import createStore from 'unistore'
import React from 'react'
import {Redirect} from 'react-router-dom'
import {Header} from './components/Header'
import axios from 'axios'

let inisialization = {
    isLogin: false,
    accKey: '',
    name: '',
    email: '',
    category: 'math',
    keyWord: 'math',
    categoryOrSearch: '',
    namaPengguna: '',
    kataKunci: '',
    judulBeritaTerkini: '',
    isLoading: '',
}

export let store = createStore(inisialization)

export let actions = store => (
{
    searchByWord: async (state, something, event) => {
        await store.setState({keyWord: something, categoryOrSearch: 'search'})
        console.warn('check state value of keyword', inisialization.keyWord)
    }
},
{
    changeInput: (state, event) => {
        console.warn("check event target", event.target.value);
        store.setState({ [event.target.name]: event.target.value });
    }
},
{
    onSubmit: (state, event) => {
        event.preventDefault()
    }
},
{
    onChange: (state, event) => {
        console.warn(event.target.value)
        store.setState({[event.target.name]: event.target.value})
    }
}
)
